import React from "react";

function CreateArea() {
    const [inputNote, setInputNote]= React.useState({
        noteTitle: "",
        noteContent: ""
    });

    function handleInput(event) {
        const {noteTitle, noteContent} = event.target.value;
        setInputNote(noteTitle, noteContent);   
    }
  return (
    <div>
      <form>
        <input value = {inputNote} name="title" placeholder="Title" />
        <textarea value = {inputNote}  name="content" placeholder="Take a note..." rows="3" />
        <button onClick={()=>{ }} >Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
