import React from "react";

function CreateArea() {
    const [inputNote, setInputNote]= React.useState({
        noteTitle: "",
        noteContent: ""
    });

  return (
    <div>
      <form>
        <input name="title" placeholder="Title" />
        <textarea name="content" placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
